import React from 'react';
import { Amplify } from 'aws-amplify';
import { Auth } from '@aws-amplify/auth'; // Import Auth from @aws-amplify/auth
import awsExports from '../aws-exports';

Amplify.configure(awsExports);

const Home = () => {
  const signIn = async (role) => {
    try {
      const options = {
        customState: role, // Pass custom state (e.g., 'teacher' or 'student')
      };

      // Trigger Hosted UI
      await Auth.federatedSignIn(options);
    } catch (err) {
      console.error('Error signing in:', err);
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Sessions Platform</h1>
      <button style={styles.button} onClick={() => signIn('teacher')}>
        Teacher Sign In / Sign Up
      </button>
      <button style={styles.button} onClick={() => signIn('student')}>
        Student Sign In / Sign Up
      </button>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100vh',
    textAlign: 'center',
    padding: '20px',
    background: '#f5f5f5',
  },
  heading: {
    marginBottom: '30px',
    fontSize: '24px',
  },
  button: {
    width: '80%',
    padding: '15px',
    margin: '10px 0',
    fontSize: '18px',
    borderRadius: '5px',
    border: 'none',
    background: '#007bff',
    color: '#fff',
    cursor: 'pointer',
  },
};

export default Home;
